<?phpnamespace App\Http\Controllers;use App\Padre;use Illuminate\Http\Request;use App\Http\Requests;use Illuminate\Support\Facades\Mail;class MailController extends Controller{    public function send(Request $request)    {        $users = Padre::all();        foreach ($users as $user){            Mail::send('emails', ['user' => $user], function($message) use ($user) {                //remitente                $message->from('adm@squalo.com.mx','Squalo');                //asunto                $message->subject($user->nombre.'tenemos una promocion para ti');                //receptor                $message->to($user->email.'', $user->nombre);            });        }    }}