<?phpnamespace App\Http\Controllers;use App\Padre;use Illuminate\Http\Request;use App\Http\Requests;use File;use Illuminate\Support\Facades\Mail;use Illuminate\Support\Facades\Storage;class MailController extends Controller{    public function send(Request $request)    {        if ($request->file('file') != null) {            //$user->photo = "U" . $user->id . '.' . $request->file('photo')->getClientOriginalExtension();            //$nombre = "/usuarios/U" . $user->id . "." . $request->file("photo")->getClientOriginalExtension();            Storage::disk('local')->put('/promocion.jpg', File::get($request->file("file")));        }        $users = Padre::all();        foreach ($users as $user){            Mail::send('emails', ['user' => $user,'request' => $request], function($message) use ($user,$request) {                //remitente                $message->from('adm@squalo.com.mx','Squalo');                //asunto                $message->subject('Sr(a) '.$user->nombre.' '.$request->input('titulo'));                //receptor                $message->to($user->email.'', $user->nombre);            });        }    }}